$(document).ready(function(){$("#btnSaveInvoice").click(function(){if(($("#cusName").val()=="")||($("#cusName").val()=="-")){alert("กรุณาใส่ ชื่อ-นามสกุลลูกค้า !");$("#cusName").focus();return false}else{if(($("#cusAddress").val()=="")||($("#cusAddress").val()=="-")){alert("กรุณาใส่ ที่อยู่ลูกค้า !");$("#cusAddress").focus();return false}else{if(($("#cusTaxID").val()=="")||($("#cusTaxID").val()=="-")){alert("กรุณาใส่ เลขที่ผู้เสียภาษี / Passport ลูกค้า !");$("#cusTaxID").focus();return false}else{var a="ต้องการบันทึกใบกำกับภาษีนี้ ใช่หรือไม่ !";return confirm(a)}}}});$("#cusTelephone").keyup(function(b){if(b.keyCode==13){var a=$.trim($(this).val());if(a!=""){check_customer(a)}}})});function check_customer(a){if(a!=""){$.ajax({type:"POST",url:link_customer,data:{telephone:a},dataType:"json",success:function(b){if(b.posc_id>0){$("#customer_id").val(b.posc_id);$("#cusName").val(b.posc_name);$("#cusAddress").val(b.posc_address);$("#cusTaxID").val(b.posc_taxid)}else{alert("ไม่พบ ข้อมูลลูกค้า ที่ต้องการ");$("#customer_id").val("0");$("#cusTelephone").val("");$("#cusName").val("");$("#cusAddress").val("");$("#cusTaxID").val("")}},error:function(c,b){alert("ไม่พบ ข้อมูลลูกค้า ที่ต้องการ !!!");$("#customer_id").val("0");$("#cusTelephone").val("");$("#cusName").val("");$("#cusAddress").val("");$("#cusTaxID").val("")}})}};